class DOMUtils {
  static getScrollParentElement(element) {
    if (element === document.body) {
      return null;
    }
    
    const style = window.getComputedStyle(element);
    if (['x', 'y'].some(position => /^(auto|scroll)$/.test(style.getPropertyValue(`overflow-${position}`)))) {
      return element;
    }
    
    return DOMUtils.getScrollParentElement(element.parentNode);
  }
}

export default DOMUtils;
